package com.example.aspect;import com.fasterxml.jackson.databind.ObjectMapper;import org.aspectj.lang.annotation.Aspect;import org.springframework.core.annotation.Order;import org.springframework.stereotype.Component;/** * 接口日志面向切面. * * @author 易富军 * @Aspect 声明这是一个面向切面类之后可以使用前置通知，后置..... * @Component 把这个类注入spring容器中，使用完这个注解之后，这个类就不属于各种归类(@controller、@server) * @Order(99)  这个注解使用来控制配置类的加载顺序  （括号里面的数字越小，加载顺序越快） */@Component@Aspect@Order(99)public class LogAspect {    /**     *      */    private static ThreadLocal<Long> STARTTIME = new ThreadLocal<>();    private static ThreadLocal<String> URL = new ThreadLocal<>();    private static final ObjectMapper MAPPER = new ObjectMapper();        }