package com.example.swaggerconfig;import io.swagger.annotations.ApiImplicitParam;import io.swagger.annotations.ApiOperation;import org.junit.Test;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * 测试Swagger. * * @author 易富军 */@RestControllerpublic class TextController {    public class UserController {        @ApiOperation(value = "接口的功能介绍",notes = "提示接口使用者注意事项",httpMethod = "GET")        @ApiImplicitParam(dataType = "string",name = "name",value = "姓名",required = true)        @RequestMapping(value = "/")        public String index(String name) {            return "hello "+ name;        }    }        @Test    public void update() {        String aaa="[0228282 87327391 2813192]";        String[] str = aaa.split(" ");        for(String x:str){            System.out.println(x);        }    }    }