package com.example.thread;/** * 异步线程. * * @author 易富军 */public class SendMailTaskThread {    public static void startThread() {        System.out.println("线程Id" + Thread.currentThread().getId());        Runnable runnable = new MyTask();        new Thread(runnable).start();        System.out.println("结束");    }        static class MyTask implements Runnable {                public MyTask() {            super();        }        @Override        public void run() {            System.out.println("业务开始" + Thread.currentThread().getId());            try {                System.out.println(" 具体业务 ");                Thread.sleep(3000);            } catch (Exception e) {                e.printStackTrace();            }            System.out.println("业务结束" + Thread.currentThread().getId());        }    }}